CREATE TABLE `movies` (
  `country` VARCHAR(1024),
  `intro` TEXT,
  `vote_num` VARCHAR(1024),
  `movie_title` VARCHAR(1024),
  `starring` VARCHAR(1024),
  `comment_num` VARCHAR(1024),
  `rating_per_stars4` VARCHAR(1024),
  `language` VARCHAR(1024),
  `directedBy` VARCHAR(1024),
  `movie_id` BIGINT PRIMARY KEY,
  `runtime` VARCHAR(1024),
  `rating_per_stars3` VARCHAR(1024),
  `release_date` VARCHAR(1024),
  `rating_per_stars2` VARCHAR(1024),
  `rating_per_stars5` VARCHAR(1024),
  `rating_num` DOUBLE,
  `genre` VARCHAR(1024),
  `rating_per_stars1` VARCHAR(1024),
  `question_num` VARCHAR(1024),
  `img_url` VARCHAR(1024),
  `abstract` VARCHAR(1024)
);


CREATE TABLE users (
  user_name VARCHAR(255) PRIMARY KEY,
  `password` VARCHAR(255) NOT NULL
);

CREATE TABLE bookmarks (
  user_name VARCHAR(255) NOT NULL,
  movie_id BIGINT NOT NULL,
  status ENUM('WAANA', 'WATCHED') NOT NULL,
  FOREIGN KEY (user_name) REFERENCES users(user_name) ON DELETE CASCADE ON UPDATE CASCADE,
  FOREIGN KEY (movie_id) REFERENCES movies(movie_id) ON DELETE CASCADE ON UPDATE CASCADE,
  PRIMARY KEY (user_name, movie_id)
);

CREATE TABLE comments (
  comment_id int AUTO_INCREMENT PRIMARY KEY,
  user_name VARCHAR(255) NOT NULL,
  movie_id BIGINT NOT NULL,
  content TEXT NOT NULL,
  time DATETIME NOT NULL,
  comment_to BIGINT DEFAULT NULL,
  FOREIGN KEY (user_name) REFERENCES users(user_name) ON DELETE CASCADE ON UPDATE CASCADE,
  FOREIGN KEY (movie_id) REFERENCES movies(movie_id) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE commentslikes (
  user_name VARCHAR(255) NOT NULL,
  comment_id int NOT NULL,
  FOREIGN KEY (user_name) REFERENCES users(user_name) ON DELETE CASCADE ON UPDATE CASCADE,
  FOREIGN KEY (comment_id) REFERENCES comments(comment_id) ON DELETE CASCADE ON UPDATE CASCADE,
  PRIMARY KEY (user_name, comment_id)
);

CREATE TABLE stars (
  user_name VARCHAR(255) NOT NULL,
  movie_id BIGINT NOT NULL,
  start int NOT NULL,
  FOREIGN KEY (user_name) REFERENCES users(user_name) ON DELETE CASCADE ON UPDATE CASCADE,
  FOREIGN KEY (movie_id) REFERENCES movies(movie_id) ON DELETE CASCADE ON UPDATE CASCADE,
  PRIMARY KEY (user_name, movie_id)
);

DROP TABLE bookmarks;
DROP TABLE comments;
DROP TABLE starts;
DROP TABLE commentslikes;
DROP TABLE movies;
DROP TABLE users;

